import React, {useState} from 'react';
import axios from 'axios';
import Webcam from 'react-webcam';
// imort logo from 'src\assets\white.png';

const WebcamCapture = () => {
  const webcamRef = React.useRef(null);
  const videoConstraints = {
    width : 200,
    height : 200,
    facingMode: 'user'
  };
  const[name, setName] = useState('')
  const capture = React.useCallback(
  () => {
    const imageSrc = webcamRef.current.getScreenshot();
    console.log(`imageSrc = ${imageSrc}`)
                //for deployment, you should put your backend url / api
    axios.post('http://127.0.0.1:5000/api', {data : imageSrc})
    	  .then(res => {
      	  console.log(`response = ${res.data}`)
      	  setName(res.data)
    })
    	  .catch(error => {
      	  console.log(`error = ${error}`)
    })
  }, 
   [webcamRef]
  );
  
  return (
  <div style={{position: "absolute", display: "flex", flexDirection: "column", marginTop: "-70vh", alignItems: "center", borderRadius: "10px", marginLeft: "35vw"}}>
    <div><Webcam
     audio = {false}
	 height = {300}
	 ref = {webcamRef}
	 screenshotFormat = "image/jpeg"
	 width = {350}
	 videoConstraints = {videoConstraints}
	/></div>
    <div style={{ background: "#E83D5E", border: "2px solid #E83D5E"}}><p style={{position: "absolute", marginTop: "", alignItems: "center", background: "#E83D5E", border: "2px solid #E83D5E"}}><button onClick={capture}>MIRROR it!</button></p></div>
	<h2>{name}</h2>
  </div>
	);
  
};

export default WebcamCapture;
